$_MODULE['<{mondialrelay}prestashop>content_50210436fc7e78da4182f21963b3a136'] = 'Depuis 1997, Mondial Relay est le spécialiste de la livraison de colis aux particuliers avec le plus grand réseau européen de livraison du e-commerce, l\'un des leaders de la distribution des colis du e-commerce en France, en Europe et accompagne plus de 40 000 e-commerçants. Avec des solutions de distribution de colis, en France et en Europe, en Points Relais, en Drive et à Domicile.';
$_MODULE['<{mondialrelay}prestashop>content_ff390019e65d83b32ddc50435476c2fe'] = 'Proposez à vos clients une offre de livraison économique simple, sûre et pratique :';
$_MODULE['<{mondialrelay}prestashop>content_4a3366d5a627724bf9a32f4b6d6ed0ee'] = '[1] Economique [/1] : Fini le monopole de l\'envoi de colis, des tarifs à partir de 3.79Eur HT !';
$_MODULE['<{mondialrelay}prestashop>content_151207df96a4e8553fda925fe5200b21'] = '[1] Simple [/1] : Envoyer ou recevoir un colis n\'a jamais été aussi simple avec toutes les solutions offertes par Mondial Relay. Le retrait en Point Relais est de plus en plus plébiscité par les clients';
$_MODULE['<{mondialrelay}prestashop>content_dadf83db013c1824f0a44d269c5f83d7'] = '[1] Sûre et pratique [/1] : Mondial Relay prend soin de vos expéditions. Avec un tracing rigoureux, le client peut suivre son colis à la trace et le retirer dans l\'un de nos Points Relais ou à domicile à l\'international';
$_MODULE['<{mondialrelay}prestashop>content_17ee3ace47ac0bfecdf46ce2c0ee56e5'] = 'Mondial Relay est évalué chaque jour par des centaines de clients par l\'intermédiaire de l\'organisme certifié AFNOR Avis Vérifiés et a une note de 4.4/5';
$_MODULE['<{mondialrelay}prestashop>content_9f35639eb56fc42b6a5731a474d810bc'] = 'Découvrez les offres Mondial Relay';
